package com.skillcheck202408.www
import java.io.File

class App {
    val greeting: String
        get() {
            return "Hello World!"
        }
}

// fun main() {
//     println(App().greeting)
// }

fun main(args: Array<String>) {
    if (args.isEmpty()) {
        println("Usage: Cat <file1> <file2> ...")
        return
    }

    for (fileName in args) {
        val file = File(fileName)
        if (file.exists() && file.isFile) {
            file.forEachLine { println(it) }
        } else {
            println("Error: File '$fileName' not found or is not a file")
        }
    }
}